import * as React from "react";
import { Link } from "react-router-dom";
import { motion } from "framer-motion";
import ProgressiveImage from "react-progressive-image";
import styled from "styled-components";

const transition = { duration: 0.6, ease: [0, 0.13, 0.23, 1] };

const Home = ({ imageDetails, image }) => (
  <>
    <StyledHome>
      <div className='container'>
        <div className='row center'>
          <div className='image-container'>
            <div
              className='thumbnail'
              ref={image}
              style={{
                width: imageDetails.width,
                height: imageDetails.height,
              }}>
              <div className='frame'>
                <Link to={`/model/`}>
                  <ProgressiveImage
                    src={require("../images/2.jpeg")}
                    placeholder={require("../images/2.jpeg")}>
                    {(src) => (
                      <motion.img
                        src={src}
                        alt='Yasmeen Tariq'
                        whileHover={{ scale: 1.1 }}
                        transition={transition}
                      />
                    )}
                  </ProgressiveImage>
                </Link>
              </div>
            </div>
            <motion.div
              exit={{ opacity: 0 }}
              transition={transition}
              className='information'>
              <div className='title'>Sophie Honcharyck</div>
              <div className='location'>
                <span>28.538336</span>
                <span>-81.379234</span>
              </div>
            </motion.div>
          </div>
        </div>
      </div>
    </StyledHome>
  </>
);

const StyledHome = styled.main`

    .container {
        position: relative;

        .row {
            height: 100vh;
            align-items: center;

            .image-container {
                position: relative;

                .thumbnail {
                    overflow: hidden;
                    position: relative;

                    .frame {
                        img {
                            width: 100%;
                        }
                    }
                }

                .information {
                    position: absolute;
                    width: 100%;
                    display: flex;
                    align-items: center;
                    justify-content: space-between;
                    margin-top: 20px;
                    text-transform: uppercase;

                    .location {
                        span:nth-child(2) {
                            margin-left: 8px;
                        }
                    }
                }
            }
        }
    }
`

export default Home;
