import React, { useEffect, useState } from "react";
import { motion, useViewportScroll, useTransform } from "framer-motion";
import styled from "styled-components";

const transition = { duration: 1.4, ease: [0.6, 0.01, -0.05, 0.9] };

const firstName = {
  initial: {
    y: 0,
  },
  animate: {
    y: 0,
    transition: {
      delayChildren: 0.6,
      staggerChildren: 0.04,
      staggerDirection: -1,
    },
  },
};

const lastName = {
  initial: {
    y: 0,
  },
  animate: {
    y: 0,
    transition: {
      delayChildren: 0.6,
      staggerChildren: 0.04,
      staggerDirection: 1,
    },
  },
};

const letter = {
  initial: {
    y: 400,
  },
  animate: {
    y: 0,
    transition: { duration: 1, ...transition },
  },
};

const Model = ({ imageDetails }) => {
  const { scrollYProgress } = useViewportScroll();
  const scale = useTransform(scrollYProgress, [0, 1], [1, 1.15]);
  const opacity = useTransform(scrollYProgress, [0, 1], [1, .8]);

  const [canScroll, setCanScroll] = useState(false);

  useEffect(() => {
    if (canScroll === false) {
      document.querySelector("body").classList.add("no-scroll");
    } else {
      document.querySelector("body").classList.remove("no-scroll");
    }
  }, [canScroll]);

  return (
    <StyledModel
      onAnimationComplete={() => setCanScroll(true)}
      className='single'
      initial='initial'
      animate='animate'
      exit='exit'>
      <div className='container fluid'>
        <div className='row center top-row'>
          <div className='top'>
            <motion.div
              initial={{ opacity: 0, y: 20 }}
              animate={{
                opacity: 1,
                y: 0,
                transition: { delay: 1.2, ...transition },
              }}
              className='details'>
              <div className='location'>
                <span>28.538336</span>
                <span>-81.379234</span>
              </div>
              <div className='mua'>MUA: @mylifeascrystall</div>
            </motion.div>
            <motion.div className='model'>
              <motion.span className='first' variants={firstName}>
                <motion.span variants={letter}>S</motion.span>
                <motion.span variants={letter}>o</motion.span>
                <motion.span variants={letter}>p</motion.span>
                <motion.span variants={letter}>h</motion.span>
                <motion.span variants={letter}>i</motion.span>
                <motion.span variants={letter}>a</motion.span>
              </motion.span>
              <motion.span className='last' variants={lastName}>
                <motion.span variants={letter}>H</motion.span>
                <motion.span variants={letter}>o</motion.span>
                <motion.span variants={letter}>n</motion.span>
                <motion.span variants={letter}>c</motion.span>
                <motion.span variants={letter}>h</motion.span>
                <motion.span variants={letter}>a</motion.span>
                <motion.span variants={letter}>r</motion.span>
                <motion.span variants={letter}>u</motion.span>
                <motion.span variants={letter}>k</motion.span>
              </motion.span>
            </motion.div>
          </div>
        </div>
        <div className='row bottom-row'>
          <div className='bottom'>
            <motion.div className='image-container-single'>
              <motion.div
                initial={{
                  y: "-50%",
                  width: imageDetails.width,
                  height: imageDetails.height,
                }}
                animate={{
                  y: 0,
                  width: "100%",
                  height: window.innerWidth > 1440 ? 400 : 130,
                  transition: { delay: 0.2, ...transition },
                }}
                className='thumbnail-single'>
                <motion.div
                  className='frame-single'
                  whileHover='hover'
                  transition={transition}>
                  <motion.img

                    src={require("../images/2.jpeg")}
                    alt='an image'
                    style={{ scale: scale, opacity: opacity  }}
                    initial={{ scale: 1.0 }}
                    animate={{
                      transition: { delay: 0.2, ...transition },
                      y: window.innerWidth > 1440 ? -200 : -40,
                    }}
                  />
                </motion.div>
              </motion.div>
            </motion.div>
          </div>

        </div>
      </div>
      <div className='detailed-information'>
        <div className='container'>
          <div className='row'>
            <h2 className='title'>
              The insiration behind the artwork & <br /> what it means.
            </h2>
            <p>
              Contrary to popular belief, Lorem Ipsum is not simply random text.
              It has roots in a piece of classical Latin literature from 45 BC,
              making it over 2000 years old. Richard McClintock, a Latin
              professor at Hampden-Sydney College in Virginia, looked up one of
              the more obscure Latin words, consectetur, from a Lorem Ipsum
              passage, and going through the cites of the word in classical
              literature, discovered the undoubtable source. Lorem Ipsum comes
              from sections 1.10.32 and 1.10.33 of "de Finibus Bonorum et
              Malorum" (The Extremes of Good and Evil) by Cicero, written in 45
              BC. This book is a treatise on the theory of ethics, very popular
              during the Renaissance. The first line of Lorem Ipsum, "Lorem
              ipsum dolor sit amet..", comes from a line in section 1.10.32.
            </p>
          </div>
        </div>
      </div>
    </StyledModel>
  );
};

export default Model;

const StyledModel = styled(motion.div)`
    .container {
        .top-row {
            height: 50vh;
            width: 100%;
            align-items: flex-end;

            .top {
                padding-bottom: 40px;

                .details {
                    display: flex;
                    align-items: center;
                    justify-content: space-between;

                    .location {
                        span:nth-child(2) {
                            margin-left: 16px;
                        }
                    }

                    .mua {
                        text-transform: uppercase;
                    }
                }

                .model {
                    overflow: hidden;

                    .first {
                        margin-right: 72px;
                    }

                    span {
                        display: inline-block;
                        position: relative;
                        font-weight: 400;
                        font-size: 144px;

                        @media (max-width: 1440px) {
                            font-size: 68px;
                        }
                    }
                }
            }
        }

        .bottom-row {
            height: 50vh;
            position: relative;

            .bottom {
                height: 100%;
                width: 100%;

                .thumbnail-single {
                    width: 100%;
                    height: 800px;
                    margin: 0 auto;
                    overflow: hidden;
                    position: absolute;
                    left: 0;
                    right: 0;

                    .frame-single {
                        img {
                            position: absolute;
                            width: 100%;
                        }
                    }
                }
            }

            .scroll-for-more {
                position: absolute;
                bottom: 200px;
                left: 200px;
                z-index: 20;

                .icon {
                    svg {
                        height: auto;
                        width: 28px;
                    }
                }

                .text {
                    margin-top: 8px;
                    color: $white;
                    text-transform: uppercase;
                }
            }
        }
    }

.detailed-information {
    margin-top: 200px;
    height: 600px;

    .container {
        .row {
            justify-content: space-between;
            align-items: flex-start;

            h2 {
                font-size: 28px;
            }

            p {
                font-size: 16;
                line-height: 28px;
                font-weight: 400;
                width: 800px;
            }
        }
    }
}
`
