import React from 'react'
import { motion } from 'framer-motion';
import styled from 'styled-components';

const firstBox = {
  hidden: { y: "100%" },
  visible: { y: 0, transition: { duration: 1 } },
  stop: { y: 0, transition: { duration: 0.5 } },
  top: { y: "-200%", transition: { duration: 0.5 } },
  exit: { y: "100%", transition: { duration: 1 } }
};

const secondBox = {
  hidden: { y: "-100%" },
  visible: { y: 0, transition: { duration: 1 } },
  stop: { y: 0, transition: { duration: 0.5 } },
  top: { y: "200%", transition: { duration: 0.5 } },
  exit: { y: "-100%", transition: { duration: 1 } }
};


const Photos = () => {
  const handleBoxStop = (box) => {
    // Trigger the top animation after the stop animation has finished
    box.start("top");
  };

  return (
    <StyledPhotos>
      <motion.div
        className="first-box box"
        variants={firstBox}
        initial="hidden"
        animate="visible"
        exit="exit"
        transition={{ duration: 0.5 }}
        onAnimationComplete={() => handleBoxStop(firstBox)}
      />
      <motion.div
        className="second-box box"
        variants={secondBox}
        initial="hidden"
        animate="visible"
        exit="exit"
        transition={{ duration: 0.5 }}
        onAnimationComplete={() => handleBoxStop(secondBox)}
      />
    </StyledPhotos>
  )
}

const StyledPhotos = styled(motion.div)`
  .box{
    position: absolute;
    width: 50%;
    height: 100px;
    background-color: black;
  }
  .second-box{
    right: 0;
  }
`
export default Photos
