import * as React from "react";
import { Link, useLocation } from "react-router-dom";
import { animate, motion } from "framer-motion";
import ProgressiveImage from "react-progressive-image";
import styled from "styled-components";
import LettersAnimation from "../components/Code";

const transition = { duration: .6, ease: [0, 0.13, 0.23, 1] };

const Home = ({ imageDetails, image , setCursorVariant}) => {
  const location = useLocation();

  const imageAnim = {
    initial: {
      opacity: 0,
    },
    animate: {
      opacity: 1,
      transition: {
        duration: 1
      }
    },
    exit: location.pathname === "/beautifulMe" ? {
      opacity: 0,
      transition: {
        duration: 1
      }
    } : undefined
  }

  return (
    <>
      <StyledHome>
        <div className='container'>
          <div className='row center'>
            <div className='image-container'>
              <div
                className='thumbnail'
                ref={image}
                style={{
                  width: imageDetails.width,
                  height: imageDetails.height,
                }}>
                <div className='frame'>
                  <Link to={`/model/`} onMouseEnter={()=>setCursorVariant("image")} onMouseLeave={()=>setCursorVariant("default")}>
                    <ProgressiveImage 
                      src={require("../images/3.jpg")}
                      placeholder={require("../images/3.jpg")}>
                      {(src) => (
                        <motion.img
                          src={src}
                          alt='Yasmeen Tariq'
                          whileHover={{ scale: 1.1 }}
                          variants={imageAnim}
                          initial="initial"
                          animate="animate"
                          exit="exit"
                          // transition={transition}
                        />
                      )}
                    </ProgressiveImage>
                  </Link>
                </div>
              </div>
              <motion.div
                exit={{ opacity: 0 }}
                transition={transition}
                className='information'>
                <div className='title' onMouseEnter={()=>setCursorVariant("image")} onMouseLeave={()=>setCursorVariant("default")}>Sophie Honcharyck</div>
                <div className='location' onMouseEnter={()=>setCursorVariant("image")} onMouseLeave={()=>setCursorVariant("default")}>
                  <LettersAnimation text={28.538336}/>
                  <LettersAnimation text={28.538336}/>
                </div>
              </motion.div>
            </div>
          </div>
        </div>
      </StyledHome>
    </>
  );
};

export default Home;


const StyledHome = styled.main`

    .container {
        position: relative;

        .row {
            height: 100vh;
            align-items: center;

            .image-container {
                position: relative;

                .thumbnail {
                    overflow: hidden;
                    position: relative;

                    .frame {
                        img {
                            width: 100%;
                        }
                    }
                }

                .information {
                    position: absolute;
                    width: 100%;
                    display: flex;
                    align-items: center;
                    justify-content: space-between;
                    margin-top: 20px;
                    text-transform: uppercase;

                    .location {
                        display: flex;
                        gap: 15px;
                    }
                }
            }
        }
    }
`
