import React, { useState, useEffect } from 'react';

const rand = (min, max) => Math.floor(Math.random() * (max - min + 1)) + min;

const MagicStars = () => {
  const [currentStarIndex, setCurrentStarIndex] = useState(0);

  useEffect(() => {
    let intervalID;

    const animate = star => {
      star.style.setProperty("--star-left", `${rand(-10, 100)}%`);
      star.style.setProperty("--star-top", `${rand(-40, 80)}%`);

      star.style.animation = "none";
      const offsetHeight = star.offsetHeight;
      star.style.animation = "";
    }

    const stars = document.getElementsByClassName("magic-star");
    const animateCurrentStar = () => {
      if (stars[currentStarIndex]) {
        animate(stars[currentStarIndex]);
      }
      setCurrentStarIndex((currentStarIndex + 1) % stars.length);
    };

    intervalID = setInterval(animateCurrentStar, 1000);

    return () => {
      clearInterval(intervalID);
    };
  }, []);

  return (
    <div>
      {/* Add your magic stars as child elements with the class "magic-star" */}
    </div>
  );
}

export default MagicStars;
